import React, { Component } from 'react'
import { connect } from 'react-redux'
import Income from '../components/Income'
import { deleteIncome } from '../actions'
import CreateIncome from './CreateIncome';

function IncomeList({ incomes, onDelete }) {
    if (!incomes.length) {
        return (
            <div>
                No posts</div>
        )
    }

console.log(incomes[1].amount)
    function sum(obj) {
        var sum = 0;
        for (var el in obj) {
            if (obj.hasOwnProperty(el)) {
                sum += parseFloat(obj[el]);
            }
        }
        return sum;
    }

    var sample = { a: 1, b: 2, c: 3 };
    var summed = sum(incomes.amount);
    console.log("sum: " + summed);
// const count = () =>{

// for(let i =0; i < incomes.length; i++){
//    let sum = incomes[i].amount
//    return sum
// }
// }
// console.log(count)
    return (
        <div className='App'>
            <div className="split expenses_lising">
            {incomes.map(income => {
                return (
                    <Income income={income} onDelete={onDelete} key={income.id} />
                )
            })}
            </div>
            <div className="split App_Form">
            <CreateIncome/>
            </div>
        </div>
    )
}
const mapStateToProps = state => {
    return {
        incomes: state.incomes
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onDelete: id => {
            dispatch(deleteIncome(id))
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(IncomeList)
