//INCOMES
export const createIncome = ({ from_where, amount }) => {
    return (dispatch) => {
        return axios.post(`${apiInc}`, { from_where, amount })
            .then(response => {
                dispatch(createIncomeSuccess(response.data))
            })
            .catch(error => {
                throw (error);
            });
    };
};

export const createIncomeSuccess = (data) => {
    return {
        type: ADD_INCOME,
        payload: {

            from_where: data.from_where,
            amount: data.amount
        }
    }
};

export const deleteIncomeSuccess = id => {
    return {
        type: DELETE_INCOME,
        payload: {
            id
        }
    }
}

export const deleteIncome = id => {
    return (dispatch) => {
        return axios.get(`${apiInc}${id}/`)
            .then(response => {
                dispatch(deleteIncomeSuccess(response.data))
            })
            .catch(error => {
                throw (error);
            });
    };
};

export const fetchIncomes = (incomes) => {
    return {
        type: FETCH_INCOME,
        incomes
    }
};

export const fetchAllIncomes = () => {
    return (dispatch) => {
        return axios.get(apiInc)
            .then(response => {
                dispatch(fetchIncomes(response.data))
            })
            .catch(error => {
                throw (error);
            });
    };
};
