//INCOMES
export const createIncome = ({ from_where, amount }) => {
    return (dispatch) => {
        return axios.post(`${apiInc}`, { from_where, amount })
            .then(res => {
                dispatch({
                    type: ADD_INCOME,
                    payload: {
                        from_where: res.data.from_where,
                        amount: res.data.amount
                    }
                })
            })
    }
}
export const deleteIncome = id => {
    return (dispatch) => {
        return axios.delete(`${apiInc}${id}/`)
            .then(res => {
                dispatch({
                    type: DELETE_INCOME,
                    payload: {
                        id:res.data.id
                    }
                })
            })
            .catch(error => {
                throw (error)
            })
    }
}
export const fetchIncomes = (income) => {
    return {
        type: FETCH_INCOME,
        income
    }
}



export const fetchAllIncomes = () => {
    return (dispatch) => {
        return axios.get('http://127.0.0.1:8000/incomes/')
            .then(res => {
               dispatch(fetchIncomes(res.data))
            })
            // .catch(error => {
