import React, { Component } from 'react'
import { connect } from 'react-redux'
import Income from '../components/Income'
import { deleteIncome } from '../actions'
import CreateIncome from './CreateIncome';

function IncomeList({ incomes, onDelete }) {
    if (!incomes.length) {
        return (
            <div>
                No posts</div>
        )
    }

console.log(incomes[1].amount)


    var friends = [{
        name: 'Anna',
        books: ['Bible', 'Harry Potter'],
        age: 21
    }, {
        name: 'Bob',
        books: ['War and peace', 'Romeo and Juliet'],
        age: 26
    }, {
        name: 'Alice',
        books: ['The Lord of the Rings', 'The Shining'],
        age: 18
    }];

    // allbooks - list which will contain all friends' books +  
    // additional list contained in initialValue
    var allbooks = friends.reduce(function (accumulator, currentValue) {
        return [...accumulator, ...currentValue.age];
    },['0']);

    console.log(allbooks)
// const count = () =>{

// for(let i =0; i < incomes.length; i++){
//    let sum = incomes[i].amount
//    return sum
// }
// }
// console.log(count)
    return (
        <div className='App'>
            <div className="split expenses_lising">
            {incomes.map(income => {
                return (
                    <Income income={income} onDelete={onDelete} key={income.id} />
                )
            })}
            </div>
            <div className="split App_Form">
            <CreateIncome/>
            </div>
        </div>
    )
}
const mapStateToProps = state => {
    return {
        incomes: state.incomes
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onDelete: id => {
            dispatch(deleteIncome(id))
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(IncomeList)
