import React, { Component } from 'react'
import { connect } from 'react-redux'

 class Info extends Component {

  render() {
    console.log(this.props.incomes[1].amount)
    var initialValue = 0;
    var sum = this.props.incomes.reduce(function (accumulator, currentValue) {
      return accumulator + currentValue.amount;
    }, initialValue)

    console.log(sum)
    return (
      <div>
        Hello, the info from portal
        The total amount of all incomes: {sum}
      </div>
    )
  }
}
const mapStateToProps = state => {
  return {
    incomes: state.incomes
  }
}

export default connect(mapStateToProps)(Info)
