import React, { Component } from 'react'

export default class NewPost extends Component {
    constructor() {
        super()
        this.state = {
            title: '',
            category: '',
            amount: ''
        }
    }
    handleInputChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = e => {
        e.preventDefault();
        //trim remove white space
        if (this.state.title.trim() && this.state.category.trim() && this.state.amount.trim()) {
            this.props.onAddPost(this.state);
            this.handleReset();
        }
    };
    handleReset = () =>{
        this.setState({
            title:'',
            category:'',
            amount:''
        })
    }

  render() {
    return (
      <div>
            <form onSubmit={this.handleSubmit} className='formExpenses'>
        <div>
            <input type='text'
                        placeholder="Title"
                        className="form-group"
                        name="title"
                        onChange={this.handleInputChange}
                        value={this.state.title}
                    />
        </div>
                <div>
                    <input type='text'
                        placeholder="category"
                        className="form-group"
                        name="category"
                        onChange={this.handleInputChange}
                        value={this.state.category}
                    />
                </div>
                <div>
                    <input type='text'
                        placeholder="Amount"
                        className="form-group"
                        name="amount"
                        onChange={this.handleInputChange}
                        value={this.state.amount}
                    />
                </div>
                <div className="form-group">
                    <button type="submit" className="btn-save">Add Post</button>
                    <button type="button" className="btn-save" onClick={this.handleReset}>
                        Reset
            </button>
                </div>
      </form>

      </div>
    )
  }
}
